import React, { useState, useEffect } from "react";
import LanguageCard from "../components/LanguageCard";
import "../styles/Languages.css";

const LanguagesPage = () => {
  const [languages, setLanguages] = useState([]); // Store the list of languages
  const [searchQuery, setSearchQuery] = useState(""); // Store the search query

  useEffect(() => {
    // Fetch the languages from the SimpleLocalize API
    const fetchLanguages = async () => {
      try {
        const response = await fetch("https://api.simplelocalize.io/api/v1/languages", {
          method: "GET",
          headers: {
            "X-SimpleLocalize-Token": "7CD71778981b5B43a53F0A646310Cba76F540F37160518e8", // Replace <API_KEY> with your actual API key
            "Content-Type": "application/json",
          },
        });

        const responseData = await response.json();
        console.log(responseData);
        if (responseData.status === 200 && Array.isArray(responseData.data)) {
          // Map the API response to match the structure expected by the component
          const formattedLanguages = responseData.data.map((language) => ({
            name: language.key, // Use `key` for name
            description: language.description, // Use `description` directly
          }));
          setLanguages(formattedLanguages);
        } else {
          console.error("Unexpected API response:", responseData);
          setLanguages([]); // Set languages to an empty array on error
        }
      } catch (error) {
        console.error("Error fetching languages:", error);
        setLanguages([]); // Set languages to an empty array on error
      }
    };
    fetchLanguages();
  }, []);

  // Filter languages based on the search query
  const filteredLanguages = Array.isArray(languages)
    ? languages.filter((language) =>
        language.name.toLowerCase().includes(searchQuery.toLowerCase())
      )
    : [];
    console.log(filteredLanguages);

  return (
    <div className="languages-page">
      <h1>Languages</h1>
      <input
        type="text"
        className="search-bar"
        placeholder="Search languages..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <div className="languages-grid">
        {filteredLanguages.length > 0 ? (
          filteredLanguages.map((language, index) => (
            <LanguageCard key={index} language={language} />
          ))
        ) : (
          <p>No languages found.</p>
        )}
      </div>
    </div>
  );
};

export default LanguagesPage;
