import React from 'react';
import '../styles/Home.css';
import tribeImage from '/public/assets/tribePaint.jpg'; // Replace with your image path
import languageImage from '/public/assets/language2.webp'; // Replace with your image path

const Home = () => {
  return (
    <div className="home-container">
      <div className="text-container">
        <h1>Welcome to Echoes of Culture</h1>
        <p>Explore and learn about tribes, endangered languages, and how to preserve them.</p>
      </div>
      <div className="image-container">
        <img src={tribeImage} alt="Tribe" className="rounded-image tribe-image" />
        <img src={languageImage} alt="Language" className="rounded-image language-image" />
      </div>
    </div>
  );
};

export default Home;
